  subdivision: function(sourceP2Ds, xLimit) {
      // point2Ds = newPoint2Ds;
    var newP2Ary = [];
    var pLen = sourceP2Ds.length;
    for (var i = 0; i < pLen; i++) {
      var p1, p2;
      if (i === 0) {
        p1 = sourceP2Ds[pLen - 1];
        p2 = sourceP2Ds[0];
      } else {
        p1 = sourceP2Ds[i - 1];
        p2 = sourceP2Ds[i];
      }
      if (p1.Y　 == 　p2.Y　 && Math.abs(p1.X - p2.X) > xLimit) {
        newP2Ary.push(p1);
        newP2Ary.push(p2);
        continue;
      }
      var Q = {
        X: 0.75 * p1.X + 0.25 * p2.X,
        Y: 0.75 * p1.Y + 0.25 * p2.Y
      };
      var P = {
        X: 0.25 * p1.X + 0.75 * p2.X,
        Y: 0.25 * p1.Y + 0.75 * p2.Y
      };

      newP2Ary.push(Q);
      newP2Ary.push(P);
    }
    return newP2Ary;
   },
  createTextPolys: function(text, size, bold, italic) {
    if(size === undefined){
      size = 20;
    }
    if(text === undefined){
      text =  "Text";
    }
    var shape = new THREE.TextGeometry(text, {
      font: 'helvetiker',
      weight: bold ? 'bold' : "normal", //normal or bold
      style: italic ? "italic" : "normal", //normal, italic
      bevelSegments: 1,
      bevelEnabled: false,
      bevelThickness: 1,
      bevelSize: 1,
      curveSegments: 1,
      size: size,
      height: 1
    });
    var xOffset = 0;
    var yOffset = 0;
    var path = [];
    var p2dPath = [];

    var exPaths = [];
    $.each(shape.textShapes, function(ts, shape) {
      var exPath = {
        "outer": [],
        "holes": [
          /*[ ]*/
        ]
      };
      $.each(shape.curves, function(i, p) {
        var pp = {
          X: p.v1.x + xOffset,
          Y: -p.v1.y + yOffset
        };
        path.push(pp);
        exPath.outer.push(pp);
        pp = {
          X: p.v2.x + xOffset,
          Y: -p.v2.y + yOffset
        };
        path.push(pp);
        exPath.outer.push(pp);
        //exPath.outer.push(pp);
      });

      OA.Utils.modifyPathOrientation(exPath.outer, true);
      var holes = [];
      $.each(shape.holes, function(i, h) {
        var hole = [];
        $.each(h.curves, function(i, p) {
          var pp = {
            X: p.v1.x + xOffset,
            Y: -p.v1.y + yOffset
          };
          hole.push(pp);
          pp = {
            X: p.v2.x + xOffset,
            Y: -p.v2.y + yOffset
          };
          hole.push(pp);
        });
        OA.Utils.modifyPathOrientation(hole, false);
        holes.push(hole);
      });

      exPath.holes = holes;
      exPaths.push(exPath);
    });
    return exPaths;
  },
